mapper.select:
	SELECT * 
	FROM 	(select inner_table.*, rownum as row_seq
        	from    (SELECT  diary_id, title, diary_addr, latitude, longitude, 
                	diary_date, start_date, end_date,
               		content, delete_date, thumbnail, group_id
                	FROM diary

               		origin_nick = #{nickname}

               			and delete_date is null


               			and delete_date is not null



               		) inner_table
      		WHERE ROWNUM < = #{search.endRowNum} )
	WHERE row_seq between #{search.startRowNum} and #{search.endRowNum}

SELECT COUNT(*) FROM (SELECT diary_id, title, diary_addr, latitude, longitude, diary_date, start_date, end_date, content, delete_date, thumbnail, group_id FROM diary
origin_nick = #{nickname}
and delete_date is null
and delete_date is not null
) countTable

SELECT diary_id, title, diary_addr, diary_date, origin_nick, start_date, end_date, thumbnail, group_id 
FROM diary
WHERE origin_nick IN 
#{userList}


		AND diary_date BETWEEN #{start} AND #{end}


		AND diary_date < #{start}


AND is_public = 't'
ORDER BY diary_date DESC

mapper.update:
UPDATE diary
SET group_id = #{groupNo}
WHERE diary_id = #{diaryNo}

