mapper.update:
UPDATE review
nickname = #{nickname}, review_addr = #{reviewPlace}, demailaddr = #{divyAddr}, content = #{reviewContents}, review_photo = #{reviewPhoto}, review_date = #{reviewDate}, star = #{star}, is_allowed = #{isAllowed}
WHERE review_id = #{reviewNo}

mapper.select:
SELECT COUNT(*) FROM (SELECT tran_no, buyer_id, user_name, cell_phone, tran_status_code FROM transaction, users
user_id = buyer_id AND buyer_id = #{buyerId}
AND prod_no LIKE '%'||#{search.searchKeyword}||'%'
AND prod_name LIKE '%'||#{search.searchKeyword}||'%'
AND price LIKE '%'||#{search.searchKeyword}||'%'
) countTable
